import { Purchase } from "./purchase";
export class User {
  constructor() {
    this.shoppingCart = [];
  }

  getUsername() {
    return this.username;
  }

  getName() {
    return this.name;
  }

  getShoppingCart() {
    return this.shoppingCart;
  }

  getIsAdmin() {
    return this.isAdmin;
  }

  setIsAdmin(isAdmin) {
    this.isAdmin = isAdmin;
  }

  setUsername(username) {
    this.username = username;
  }

  setName(name) {
    this.name = name;
  }

  addPurchase(productId, quantity) {
    for (let purchase of this.shoppingCart) {
      if (purchase.getProductId() == productId) {
        purchase.setQuantity(purchase.getQuantity() + quantity);
        return;
      }
    }

    this.shoppingCart.push(new Purchase(productId, quantity));
  }

  addPurchaseInstance(purchase) {
    for (let purchase of this.shoppingCart) {
      if (purchase.getProductId() == purchase.getProductId()) {
        purchase.setQuantity(purchase.getQuantity() + purchase.getQuantity());
        return;
      }
    }

    this.shoppingCart.push(purchase);
  }

  removePurchase(purchase) {
    let index = this.shoppingCart.indexOf(purchase);
    this.shoppingCart.splice(index, 1);
  }

  removeAllPruchases() {
    this.shoppingCart = [];
  }

}
