import { Invoice } from 'src/app/model/invoice';
import { Purchase } from 'src/app/model/purchase';
import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "../product-controller/product-controller.service";
import * as i3 from "../user-controller/user-controller.service";
export class InvoiceControllerService {
    constructor(http, productControllerService, userControllerService) {
        this.http = http;
        this.productControllerService = productControllerService;
        this.userControllerService = userControllerService;
        this.listInvoices = [];
        this.lastId = 0;
    }
    async getListInvoices() {
        var json = await this.http.get(`${environment.baseURL}/invoice/list/${this.userControllerService.getCurrentUser().getUsername()}`).toPromise();
        this.listInvoices = [];
        json.forEach((element) => {
            var listPurchases = [];
            element.listPurchases.forEach((element2) => {
                var purchase = new Purchase(element2.productId, element2.quantity);
                purchase.setId(element2.id);
                listPurchases.push(purchase);
            });
            var invoice = new Invoice(element.id, element.userId, listPurchases, element.priceTotal);
            this.listInvoices.push(invoice);
        });
        return this.listInvoices;
    }
    addInvoice(userId, listPurchases) {
        let purchaseTotal = 0;
        for (let purchase of listPurchases) {
            purchaseTotal += this.productControllerService.findProductById(purchase.getProductId()).getPrice() * purchase.getQuantity();
        }
        this.http.post(`${environment.baseURL}/create`, { userId: userId, purchaseTotal: purchaseTotal, listPurchases: listPurchases });
    }
    async findInvoicesByRangeDates(date1, date2) {
        let invoices = [];
        let json = await this.http.get(`${environment.baseURL}/list/${date1.getDate()}-${date1.getMonth()}-${date1.getFullYear()}/${date2.getDate()}-${date2.getMonth()}-${date1.getFullYear()}`).toPromise();
    }
    findInvoicesByUserId(userId) {
        let invoices = [];
        for (let invoice of this.listInvoices) {
            if (invoice.getUserId() == userId) {
                invoices.push(invoice);
            }
        }
        return invoices;
    }
    findInvoicesByUserIdAndDateRange(userId, date1, date2) {
        let invoices = [];
        for (let invoice of this.findInvoicesByRangeDates(date1, date2)) {
            if (invoice.getUserId() == userId) {
                invoices.push(invoice);
            }
        }
        return invoices;
    }
    removeInvoiceById(id) {
        let Invoice;
        Invoice = this.findInvoiceById(id);
        this.removeInvoice(Invoice);
    }
    removeInvoice(Invoice) {
        if (Invoice != null) {
            let index = this.listInvoices.indexOf(Invoice);
            this.listInvoices.splice(index, 1);
        }
    }
}
