import { Product } from 'src/app/model/product';
import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/platform-browser";
export class ProductControllerService {
    constructor(http, _sanitizer) {
        this.http = http;
        this._sanitizer = _sanitizer;
        this.listProducts = [];
        this.lastId = 0;
    }
    async getListProducts() {
        var json = await this.http.get(`${environment.baseURL}/product/`).toPromise();
        this.listProducts = [];
        json.forEach((element) => {
            var imagePath = this._sanitizer.bypassSecurityTrustResourceUrl('data:image/jpg;base64,' + element.image);
            var product = new Product(element.id, element.name, +element.price, imagePath.string());
            this.listProducts.push(product);
        });
        return this.listProducts;
    }
    addProduct(name, price, image) {
        this.listProducts.push(new Product(this.lastId, name, price, image));
        console.log(this.listProducts.length);
        this.lastId++;
    }
    modProduct(product, name, price) {
        this.listProducts.forEach(element => {
            if (element.getId() == product.getId()) {
                element.setName(name);
                element.setPrice(price);
            }
        });
    }
    deleteProduct(product) {
        this.listProducts.splice(this.listProducts.indexOf(product), 1);
        this.listProducts.filter(item => item !== product);
        console.log(this.listProducts.length);
    }
    findProductById(id) {
        for (let product of this.listProducts) {
            if (product.getId() == id) {
                return product;
            }
        }
        return null;
    }
    findProductByName(name) {
        for (let product of this.listProducts) {
            if (product.getName() == name) {
                return product;
            }
        }
        return null;
    }
    removeProductByName(name) {
        let product;
        product = this.findProductByName(name);
        if (product != null)
            this.removeProduct(product);
    }
    removeProductById(id) {
        let product;
        product = this.findProductById(id);
        this.removeProduct(product);
    }
    removeProduct(product) {
        if (product != null) {
            let index = this.listProducts.indexOf(product);
            this.listProducts.splice(index, 1);
        }
    }
}
